select "tablero"."ingresos"."id", "tablero"."ingresos"."periodo", "tablero"."ingresos"."provincia", replace(indicador,'.','|') as indicador, "numerador", "denominador", "observaciones" from "tablero.ingresos" where "tablero"."ingresos"."provincia" = "05" and "tablero"."ingresos"."periodo" = "2019-05" order by "periodo" desc, "provincia" asc, left(indicador,1)::integer asc, right(indicador,-2)::integer asc
